using NAudio.Wave;

using System;
using System.IO;

namespace EFTMap
{
    public class NPlayer
    {
        public string mokoko = "";

        private WaveOutEvent? outputDevice;
        private AudioFileReader? audioFile;
        private WaveStream? currentStream;
        
        public void Mp3(string filePath)
        {
            try
            {
                if (!File.Exists(filePath))
                {
                    Console.WriteLine("파일이 존재하지 않습니다: " + filePath);
                    return;
                }

                Stop(); // 이전 재생 중지

                audioFile = new AudioFileReader(filePath);
                outputDevice = new WaveOutEvent();

                outputDevice.Init(audioFile);
                outputDevice.Volume = 0.75f;
                outputDevice.Play();

                outputDevice.PlaybackStopped += (s, e) => Stop();
            }
            catch (Exception ex)
            {
                Console.WriteLine("오류 발생: " + ex.Message);
            }
        }

        public void Base64(string base64)
        {
            try
            {
                Stop(); // 기존 재생 정리

                byte[] mp3Bytes = Convert.FromBase64String(base64);
                var mp3Stream = new MemoryStream(mp3Bytes);
                currentStream = new Mp3FileReader(mp3Stream); // Mp3FileReader가 스트림을 내부적으로 사용함

                outputDevice = new WaveOutEvent();
                outputDevice.Init(currentStream);
                outputDevice.Volume = 0.75f;
                outputDevice.Play();
                outputDevice.PlaybackStopped += (s, e) => Stop();
            }
            catch (Exception ex)
            {
                Console.WriteLine("Base64 MP3 재생 중 오류: " + ex.Message);
            }
        }

        public void Stop()
        {
            if (outputDevice != null)
            {
                outputDevice.Stop();
                outputDevice.Dispose();
                outputDevice = null;
            }

            if (audioFile != null)
            {
                audioFile.Dispose();
                audioFile = null;
            }

            if (currentStream != null)
            {
                currentStream.Dispose();
                currentStream = null;
            }
        }
    }
}
